import React, { useState } from "react";
import { useCart } from "react-use-cart";
import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";
import "../Cart/Cart.css";

export default function Cart() {
  const [open, setOpen] = useState(false);

  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen(false);

  const {
    isEmpty,
    totalItems,
    items,
    cartTotal,
    emptyCart,
    updateItemQuantity,
    removeItem,
  } = useCart();
  console.log(items);

  const closeIcon = (
    <button type="button" className="nes-btn">
      <i className="nes-icon close is-small"></i>
    </button>
  );

  return (
    <>
      <div>
        <button
          className="nes-btn is-primary"
          type="button"
          onClick={onOpenModal}
          style={{
            width: "10%",
            height: "2%",
            position: "absolute",
            top: 30,
            right: 40,
            paddingBottom: 45,
          }}
        >
          <img src="data:image/png;base64,"></img>
          {"   "}
          <text
            style={{
              fontSize: 20,
            }}
          >
            ({totalItems})
          </text>
        </button>
        <Modal
          open={open}
          onClose={onCloseModal}
          center
          closeIcon={closeIcon}
          classNames={{
            overlay: "customOverlay",
            modal: "customModal",
          }}
        >
          <h3>Cart ({totalItems})</h3>
          {isEmpty ? (
            <p>Your cart is empty</p>
          ) : (
            <div>
              <table>
                <thead>
                  <th>
                    <h5>Item </h5>
                  </th>
                  <th>
                    <h5>Size </h5>
                  </th>
                  <th>
                    <h5>Price each</h5>
                  </th>
                  <th>
                    <h5>Quantity</h5>
                  </th>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item.id}>
                      <td> {item.title} </td>
                      <td> {item.options[0].option} </td>
                      <td> {"R" + item.price} </td>

                      <td>
                        <button
                          className="nes-btn is-primary"
                          type="button"
                          onClick={() =>
                            updateItemQuantity(item.id, item.quantity - 1)
                          }
                        >
                          -
                        </button>
                        {" " + item.quantity + " "}
                        <button
                          className="nes-btn is-primary"
                          type="button"
                          onClick={() =>
                            updateItemQuantity(item.id, item.quantity + 1)
                          }
                        >
                          +
                        </button>
                        <button
                          className="nes-btn is-error"
                          type="button"
                          onClick={() => removeItem(item.id)}
                        >
                          &times;
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <h3 className="total">Total R{cartTotal}</h3>
              <button
                className="nes-btn is-success checkOut"
                type="button"
                onClick={() => {
                  emptyCart();
                  onCloseModal();
                }}
              >
                Checkout
              </button>
            </div>
          )}
        </Modal>
      </div>
    </>
  );
}
